                //停止定时器
                clearInterval(timer);
            }
            outer.onmouseout = function () {
                arr.style.display = "none";
                //开启定时器
                timer = setInterval(autoPlay,1000);
            }
            //右侧按钮逻辑的封装
            function autoPlay() {
                //点击右侧按钮，索引值自增;
                square++;
                key++;
                //key和square不能无限制自增;
                //square到5的时候就要清0了;
                if(square === olLiArr.length){
                    square = 0;
                }
                //无缝滚动原理:第二轮从第一张滑向第二张的时候，瞬间闪动到第一张，在滑向第二张;
                //key的值最多到5，因为只有6张图片;等于6的时候瞬间闪动到第一张，在从第一张滑向第二张;
                if(key === ulLiArr.length){
                    //瞬间闪动到第一张：
                    ul.style.left = 0;
                    //滑向第二张;
                    key = 1;//第二张索引值为1;(下面点亮盒子，移动ul)
                }

                //1.点亮盒子   2.移动ul;
                for(var j=0;j<olLiArr.length;j++){
                    olLiArr[j].className = "";
                }
                //当前的变化不能用this，要用square做索引值；
                olLiArr[square].className = "current";

                // 2.移动ul;
                var sss = imgWidth*key;//图片的索引值是key；
                animate(ul,-sss);
            }
        }
        //匀速动画封装
        function animate(ele,target) {
            //要用定时器，先清除定时器;
            clearInterval(ele.timer);
            //设置定时器;
            ele.timer = setInterval(function () {
                //1.获取步长;
                var step = target > ele.offsetLeft? 10: -10;
                //2.赋值
                ele.style.left = ele.offsetLeft + step + "px";
                //3.目标位置和当前位置不足或者等于一个步长就清除定时器i;
                if(Math.abs(target-ele.offsetLeft) <= Math.abs(step)){
                    //清除定时器之前，直接设置目标位置
                    ele.style.left = target+"px";
                    clearInterval(ele.timer);
                }
            },10);
        }
    </script>
